import { Component, ChangeDetectionStrategy, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';

// Base64 encoded default logo
const DEFAULT_LOGO_BASE64 = 'data:image/jpeg;base64,'
interface Obra {
  nome: string;
  selecionavel: boolean;
}

interface ReportItem {
  id: number;
  imageName: string;
  workDescription: string;
  uploadedImage: string | null;
  approvalStatus: 'Aprovado' | 'Não aprovado' | null;
  solution: string;
}

type ReportPage = ReportItem[];

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule]
})
export class AppComponent {
  // State Signals
  companyLogo = signal<string | null>(DEFAULT_LOGO_BASE64);
  selectedObra = signal<string>('OBRA RODOLFO');
  selectedFase = signal<string>('FASE ESTRUTURA');
  selectedContratante = signal<string>('Contratante 1');
  currentPageIndex = signal<number>(0);

  private createInitialReportItems(count: number): ReportItem[] {
    return Array.from({ length: count }, (_, i) => ({
      id: Date.now() + i, // Use a more unique ID
      imageName: '',
      workDescription: '',
      uploadedImage: null,
      approvalStatus: null,
      solution: '',
    }));
  }

  pages = signal<ReportPage[]>([this.createInitialReportItems(4)]);

  // Computed signal for the items on the current page
  currentReportItems = computed(() => {
    const pages = this.pages();
    const index = this.currentPageIndex();
    if (index >= 0 && index < pages.length) {
      return pages[index];
    }
    return [];
  });

  // Data Options
  readonly obras: Obra[] = [
    { nome: 'OBRA RODOLFO', selecionavel: true },
    { nome: 'OBRA CARVALHO', selecionavel: true },
    { nome: 'OBRA ISABELA', selecionavel: true },
    { nome: 'OBRA CADU', selecionavel: false }
  ];

  readonly fases: string[] = [
    'FASE ESTRUTURA',
    'FASE CINZA',
    'FASE ACABAMENTO'
  ];

  readonly contratantes: string[] = [
    'Contratante 1',
    'Contratante 2',
    'Contratante 3',
    'Contratante 4'
  ];
  
  readonly currentDateTime: string = new Date().toLocaleString('pt-BR', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });

  // Page Handlers
  addPage() {
    if (this.pages().length < 8) {
      this.pages.update(currentPages => [...currentPages, this.createInitialReportItems(4)]);
      this.currentPageIndex.set(this.pages().length - 1);
    }
  }

  removePage(index: number, event: MouseEvent) {
    event.stopPropagation();
    if (this.pages().length > 1) {
      this.pages.update(pages => pages.filter((_, i) => i !== index));
      if (this.currentPageIndex() >= this.pages().length) {
        this.currentPageIndex.set(this.pages().length - 1);
      }
    }
  }

  goToPage(index: number) {
    this.currentPageIndex.set(index);
  }

  // Event Handlers
  onObraChange(event: Event) {
    this.selectedObra.set((event.target as HTMLSelectElement).value);
  }

  onFaseChange(event: Event) {
    this.selectedFase.set((event.target as HTMLSelectElement).value);
  }

  onContratanteChange(event: Event) {
    this.selectedContratante.set((event.target as HTMLSelectElement).value);
  }
  
  onImageNameInput(event: Event, itemIndex: number) {
    const target = event.target as HTMLInputElement;
    this.pages.update(currentPages => {
      const newPages = currentPages.map(page => page.map(item => ({...item})));
      const pageToUpdate = newPages[this.currentPageIndex()];
      pageToUpdate[itemIndex].imageName = target.value;
      return newPages;
    });
  }

  onDescriptionInput(event: Event, itemIndex: number) {
    const target = event.target as HTMLTextAreaElement;
     this.pages.update(currentPages => {
      const newPages = currentPages.map(page => page.map(item => ({...item})));
      const pageToUpdate = newPages[this.currentPageIndex()];
      pageToUpdate[itemIndex].workDescription = target.value;
      return newPages;
    });
  }

  onSolutionInput(event: Event, itemIndex: number) {
    const target = event.target as HTMLTextAreaElement;
    this.pages.update(currentPages => {
      const newPages = currentPages.map(page => page.map(item => ({...item})));
      const pageToUpdate = newPages[this.currentPageIndex()];
      pageToUpdate[itemIndex].solution = target.value;
      return newPages;
    });
  }

  onFileSelected(event: Event, itemIndex: number): void {
    const file = (event.target as HTMLInputElement).files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        this.pages.update(currentPages => {
            const newPages = currentPages.map(page => page.map(item => ({...item})));
            const pageToUpdate = newPages[this.currentPageIndex()];
            pageToUpdate[itemIndex].uploadedImage = e.target?.result as string;
            return newPages;
        });
      };
      reader.readAsDataURL(file);
    }
  }
  
  onLogoSelected(event: Event): void {
    const file = (event.target as HTMLInputElement).files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        this.companyLogo.set(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }

  setApprovalStatus(status: 'Aprovado' | 'Não aprovado', itemIndex: number): void {
    this.pages.update(currentPages => {
        const newPages = currentPages.map(page => page.map(item => ({...item})));
        const pageToUpdate = newPages[this.currentPageIndex()];
        pageToUpdate[itemIndex].approvalStatus = status;
        return newPages;
    });
  }

  printReport(): void {
    window.print();
  }
}
